// Get all items from input
const items = $input.all();

// Get thresholds from Workflow Configuration node
const config = $('Workflow Configuration').first().json;
const lowStockThreshold = config.lowStockThreshold;

// Group items by supplier
const supplierMap = {};

for (const item of items) {
  const data = item.json;
  const supplierEmail = data.supplierEmail;

  if (!supplierMap[supplierEmail]) {
    supplierMap[supplierEmail] = {
      email: supplierEmail,
      items: []
    };
  }

  supplierMap[supplierEmail].items.push(data);
}

// Process each supplier to find low stock items with lowest cost
const results = [];

for (const supplierEmail in supplierMap) {
  const supplier = supplierMap[supplierEmail];

  // Filter items with low stock (below threshold)
  const lowStockItems = supplier.items.filter(item => item.quantity < lowStockThreshold);

  if (lowStockItems.length > 0) {
    // Find the item with lowest unit cost
    const lowestCostItem = lowStockItems.reduce((min, item) => {
      return item.unitCost < min.unitCost ? item : min;
    });

    // Add to results
    results.push({
      supplierEmail: supplierEmail,
      itemName: lowestCostItem.itemName,
      quantity: lowestCostItem.quantity,
      unitCost: lowestCostItem.unitCost,
      totalCost: lowestCostItem.quantity * lowestCostItem.unitCost
    });
  }
}

// Return results in n8n format
return results.map(item => ({json: item}));

