// Get all inventory records from input
const items = $input.all();

// Get trend analysis days from Workflow Configuration
const config = $('Workflow Configuration').first().json;
const trendAnalysisDays = config.trendAnalysisDays || 30; // Default to 30 days if not set

// Group records by itemName
const itemGroups = {};

for (const item of items) {
  const data = item.json;
  const itemName = data.itemName;

  if (!itemGroups[itemName]) {
    itemGroups[itemName] = [];
  }

  itemGroups[itemName].push({
    date: new Date(data.date || data.timestamp),
    quantity: data.quantity
  });
}

// Analyze trends for each item
const results = [];

for (const itemName in itemGroups) {
  const records = itemGroups[itemName];

  // Sort records by date
  records.sort((a, b) => a.date - b.date);

  // Get current quantity (most recent record)
  const currentQuantity = records[records.length - 1].quantity;

  // Calculate average daily consumption rate
  let totalConsumption = 0;
  let daysCounted = 0;

  for (let i = 1; i < records.length; i++) {
    const consumption = records[i - 1].quantity - records[i].quantity;
    if (consumption > 0) {
      totalConsumption += consumption;
      daysCounted++;
    }
  }

  const averageDailyConsumption = daysCounted > 0 ? totalConsumption / daysCounted : 0;

  // Calculate days until stockout
  let daysUntilStockout = null;
  if (averageDailyConsumption > 0) {
    daysUntilStockout = Math.floor(currentQuantity / averageDailyConsumption);
  }

  // Determine trend (increasing/stable/declining)
  let trend = 'stable';
  if (records.length >= 2) {
    const firstHalfAvg = records.slice(0, Math.floor(records.length / 2))
      .reduce((sum, r) => sum + r.quantity, 0) / Math.floor(records.length / 2);
    const secondHalfAvg = records.slice(Math.floor(records.length / 2))
      .reduce((sum, r) => sum + r.quantity, 0) / (records.length - Math.floor(records.length / 2));

    if (secondHalfAvg > firstHalfAvg * 1.1) {
      trend = 'increasing';
    } else if (secondHalfAvg < firstHalfAvg * 0.9) {
      trend = 'declining';
    }
  }

  // Flag items that will run out within the next 7 days
  const isRunningOut = daysUntilStockout !== null && daysUntilStockout <= 7;

  results.push({
    itemName: itemName,
    currentQuantity: currentQuantity,
    averageDailyConsumption: averageDailyConsumption,
    daysUntilStockout: daysUntilStockout,
    isRunningOut: isRunningOut,
    trend: trend
  });
}

// Return results in n8n format
return results.map(item => ({json: item}));

// Get filtered inventory trends (only running out items) from Filter Items Running Out node
const inventoryTrends = $('Filter Items Running Out').all();

// Get supplier data from Read Supplier Dataset node
const supplierData = $input.all();

// Create a map of supplier data by itemName for quick lookup
const supplierMap = {};
for (const item of supplierData) {
  const data = item.json;
  supplierMap[data.itemName] = data;
}

// Merge inventory trends with supplier data (only running out items)
const mergedResults = [];

for (const inventoryItem of inventoryTrends) {
  const inventory = inventoryItem.json;
  const supplier = supplierMap[inventory.itemName];

  if (supplier) {
    // Combine data to create enriched records
    mergedResults.push({
      supplierEmail: supplier.supplierEmail,
      supplierName: supplier.supplierName,
      itemName: inventory.itemName,
      quantity: inventory.currentQuantity,
      unitCost: supplier.unitCost,
      isRunningOut: inventory.isRunningOut,
      daysUntilStockout: inventory.daysUntilStockout,
      averageDailyConsumption: inventory.averageDailyConsumption,
      trend: inventory.trend
    });
  }
}

// Return results in n8n format
return mergedResults.map(item => ({json: item}));
